import React, { Component } from 'react'
import NewsItem from './NewsItem'

export class News extends Component {


    constructor(){
        super();
        this.state ={
            articles:[],
            loading:false,
            page:1
        }
    }

    async componentDidMount(){
        let url = "https://newsapi.org/v2/everything?q=apple&from=2023-06-08&to=2023-06-08&sortBy=popularity&apiKey=6d93827683a049eba178e597bd370207"
        let data = await fetch(url);
        let parsedata = await data.json();
        console.log(parsedata);
        this.setState({articles:parsedata.articles})
    }


    handlePrevClick=()=>{

    }

    handleNextClick=()=>{

    }

  render() {
    return (
        <>
      <div className='container my-3'>
        <h1>NewMonkey - Top Headlines</h1>
        <div className="row">
            {this.state.articles.map((element)=>{
            return <div className="col-md-4 mb-2" key={element.url}>
            <NewsItem  title={element.title} discription={element.description} imgUrl={element.urlToImage}
            newsUrl={element.url}/>
            </div>
            })}
        </div>
       </div>
       
       <div className="container d-flex justify-content-between mb-4">
            <button disabled={this.state.page<=1} className="btn btn-dark" onClick={this.handlePrevClick}> &larr; Previous</button>
            <button className="btn btn-dark" onClick={this.handleNextClick}>Next &rarr;</button>
        </div>
        </>
    )
  }
}

export default News
