import React, { useEffect , useState } from 'react'
import NewsItem from './NewsItem'
import Loader from './Loader'
import PropTypes from 'prop-types'
import InfiniteScroll from "react-infinite-scroll-component";


const News = (props) => {

  const [articles, setArticles] = useState([])
  const [loading, setLoading] = useState(true)
  const [page, setPage] = useState(1)
  const [totalResults, setTotalResults] = useState(0)

 const capitalizeFirstLetter =(string)=>{
    return string.charAt(0).toUpperCase() + string.slice(1)
  }



    const updateNews = async ()=>{
      props.setProgress(10);
      let url = `https://newsapi.org/v2/top-headlines?country=${props.country}&category=${props.category}&apiKey=6d93827683a049eba178e597bd370207&page-1&pageSize=${props.pageSize}`
      setLoading(true);
      let data = await fetch(url);
      props.setProgress(30);
      let parsedata = await data.json();
      props.setProgress(70);
      setArticles(parsedata.articles)
      setTotalResults(parsedata.totalResults)
      setLoading(false)
      props.setProgress(100)
      setLoading(false)
    }

    useEffect(() => {
      updateNews()
    }, [])
    
  
    
  //  const fetchData = async () =>{
  //     this.setState({page : this.state.page+1})
  //     let url = `https://newsapi.org/v2/top-headlines?country=${props.country}&category=${props.category}&apiKey=6d93827683a049eba178e597bd370207&page-1&pageSize=${props.pageSize}`
  //     this.setState({loading:true});
  //     let data = await fetch(url);
  //     let parsedata = await data.json();
  //     this.setState({
  //       articles:this.state.articles.concat(parsedata.articles) , 
  //       totalResults: parsedata.totalResults,
  //       loading:false
  //     })
  //   }


   const handlePrevClick = async ()=>{
    setPage(page-1)
    updateNews()
    }

  const  handleNextClick = async ()=>{
    updateNews()
    setPage(page+1)
    }

    const fetchMoreData = async () =>{
      setPage(page+1)
      let url = `https://newsapi.org/v2/top-headlines?country=${props.country}&category=${props.category}&apiKey=6d93827683a049eba178e597bd370207&page-1&pageSize=${props.pageSize}`
      let data = await fetch(url);
      let parsedata = await data.json();
      setArticles(articles.concat(parsedata.articles))
      setTotalResults(parsedata.totalResults)
    }
    
    return (
        <>
      <div className='container my-3'>
        <h1 className='text-center my-3'>NewsMonkey - Top {capitalizeFirstLetter(props.category)} Headlines</h1>
        <InfiniteScroll
          dataLength={articles.length}
          next={fetchMoreData}
          hasMore={articles.length !== totalResults}
          loader={<Loader/>}
        >
          <div className="container">
        <div className="row"> 
            { articles.map((element)=>{
            return <div className="col-md-4 mb-2" key={element.url}>
            <NewsItem  title={element.title} discription={element.description} imgUrl={element.urlToImage}
            newsUrl={element.url} author={element.author} date={element.publishedAt}  source={element.source.name} />
            </div>
            })}
        </div></div>
        </InfiniteScroll>
       </div>
        </>
    )
}


News.defaultProps = {
  country : 'in',
  pageSize : 8,
  category : 'general'
}


News.propTypes = {
 country : PropTypes.string,
 pageSize : PropTypes.number,
 category : PropTypes.string
}

export default News
