import React, { Component } from 'react'
import NewsItem from './NewsItem'
import Loader from './Loader'

export class News extends Component {


    constructor(){
        super();
        this.state ={
            articles:[],
            loading:false,
            page:1
        }
    }

    async componentDidMount(){
        let url = `https://newsapi.org/v2/everything?q=apple&from=2023-06-08&to=2023-06-08&sortBy=popularity&apiKey=6d93827683a049eba178e597bd370207&page-1&pageSize=${this.props.pageSize}`
        this.setState({loading:true});
        let data = await fetch(url);
        let parsedata = await data.json();
        console.log(parsedata);
        this.setState({articles:parsedata.articles , totalResults: parsedata.totalResults})
    }


    handlePrevClick= async ()=>{
        let url = `https://newsapi.org/v2/everything?q=apple&from=2023-06-08&to=2023-06-08&sortBy=popularity&apiKey=6d93827683a049eba178e597bd370207&page=${this.state.page - 1}&pageSize=${this.props.pageSize}`
        this.setState({loading:true});
        let data = await fetch(url);
        let parsedata = await data.json();
        this.setState({loading:false});
        this.setState({articles:parsedata.articles})
      this.setState({
        page:this.state.page-1,
        articles:parsedata.articles,
        loading:false
      })
    }

    handleNextClick= async ()=>{
        if(!(this.state.page + 1 > Math.ceil(this.state.totalResults/this.props.pageSize))){
        let url = `https://newsapi.org/v2/everything?q=apple&from=2023-06-08&to=2023-06-08&sortBy=popularity&apiKey=6d93827683a049eba178e597bd370207&page=${this.state.page + 1}&pageSize=${this.props.pageSize}`
        this.setState({loading:true});
        let data = await fetch(url);
        let parsedata = await data.json();
        this.setState({loading:false});
        this.setState({articles:parsedata.articles})
      this.setState({
        page:this.state.page+1,
        articles:parsedata.articles,
        loading:false
      })
    }
    }
    
  render() {
    return (
        <>
      <div className='container my-3'>
        <h1 className='text-center'>NewMonkey - Top Headlines</h1>
        {this.state.loading && <Loader/>}
        <div className="row">
            {!this.state.loading && this.state.articles.map((element)=>{
            return <div className="col-md-4 mb-2" key={element.url}>
            <NewsItem  title={element.title.slice(0,45)} discription={element.description.slice(0,85)} imgUrl={element.urlToImage}
            newsUrl={element.url}/>
            </div>
            })}
        </div>
       </div>
       
       <div className="container d-flex justify-content-between mb-4">
            <button disabled={this.state.page<=1} className="btn btn-dark" onClick={this.handlePrevClick}> &larr; Previous</button>
            <button disabled={this.state.page + 1 > Math.ceil(this.state.totalResults/this.props.pageSize)} className="btn btn-dark" onClick={this.handleNextClick}>Next &rarr;</button>
        </div>
        </>
    )
  }
}

export default News
